[Message] from User to Mike: {
  "task": "Build a full-stack habit, career progress tracker, and job aggregator web application.",
  "role": "You are an expert full-stack developer, UI/UX designer, and system architect.",
  "goal": "Create a clean, interactive web app that helps users track habits (like DSA practice), export progress, and get notified of job opportunities. Features are prioritized into MVP, Nice-to-have, and Pro/Startup Level to allow staged development.",
  "persona": "The user is a beginner developer who needs modular, well-commented code that is easy to understand, modify, and extend.",
  "technologies": {
    "front_end": [
      "HTML5",
      "CSS3 (Tailwind CSS for utility classes)",
      "JavaScript (vanilla or React.js for interactivity)",
      "Chart.js (for progress visualization)"
    ],
    "back_end": [
      "Node.js",
      "Express.js",
      "Axios (HTTP requests)",
      "Cheerio (web scraping)",
      "node-cron (scheduling)"
    ],
    "storage": [
      "LocalStorage (for front-end persistence)",
      "JSON/SQLite (for storing job listings and progress logs)"
    ],
    "export": [
      "SheetJS (xlsx npm package) for Excel/CSV export"
    ]
  },
  "features": {
    "MVP": [
      {
        "name": "Habit & Progress Tracker",
        "description": "Users can add custom habits (e.g., 'Solve 3 DSA problems'), mark them as completed daily, and view progress with a bar or streak counter."
      },
      {
        "name": "Local Storage Persistence",
        "description": "Use LocalStorage/JSON so data remains after refresh."
      },
      {
        "name": "Job Scraper Service",
        "description": "Node.js microservice scrapes specific job portals daily with node-cron, saves unseen jobs, and avoids duplicates."
      },
      {
        "name": "Job List UI",
        "description": "Front-end dashboard tab showing latest scraped job opportunities."
      },
      {
        "name": "Data Export",
        "description": "Allow users to export habit logs and progress into Excel/CSV."
      }
    ],
    "Nice_to_have": [
      {
        "name": "Gamification",
        "description": "Streak counters, milestone badges (e.g., '7-Day Consistency Champ'), and confetti animations when daily goals are hit."
      },
      {
        "name": "Charts & Visualization",
        "description": "Weekly/monthly progress charts using Chart.js."
      },
      {
        "name": "Motivational Boost",
        "description": "Daily randomized motivational quote or coding tip."
      },
      {
        "name": "Polished UI",
        "description": "Modern, card-based UI with soft shadows, rounded edges, and smooth animations."
      },
      {
        "name": "Job Filters",
        "description": "Filter jobs by keyword, location, or company before showing them in the UI."
      }
    ],
    "Pro_Startup_Level": [
      {
        "name": "User Authentication",
        "description": "Google login to allow sync across devices."
      },
      {
        "name": "Job Notifications",
        "description": "Send new job notifications via Telegram, Email, or Push notifications."
      },
      {
        "name": "ATS Resume Helper",
        "description": "Upload JD + resume → parse keywords → highlight missing terms and score resume."
      },
      {
        "name": "Advanced Analytics",
        "description": "Weekly/monthly reports, growth trends, shareable PDFs."
      },
      {
        "name": "Multi-user & Community",
        "description": "Leaderboards, challenges, and group tracking for friends/college peers."
      }
    ]
  },
  "style_and_design": "Clean, modern, minimalistic. Light theme with soft shadows, rounded cards, and a single accent color. Keep it engaging and interactive with animations.",
  "output_format": {
    "file_structure": [
      "client/ (front-end: index.html, style.css, script.js or React components)",
      "server/ (back-end: index.js, scraper.js, cron.js, jobs.json)",
      "package.json"
    ],
    "code_comments": "Every function and key logic section should have concise, beginner-friendly comments."
  }
}

